// This is a giant hack so we can use contructors 
#define OPERAND nmd_x86_operand2
#define MEM nmd_x86_memory_operand2

struct nmd_x86_memory_operand2
{
	uint8_t segment;     /* The segment register. A member of 'NMD_X86_REG'. */
	uint8_t base;        /* The base register. A member of 'NMD_X86_REG'. */
	uint8_t index;       /* The index register. A member of 'NMD_X86_REG'. */
	uint8_t scale;       /* Scale(1, 2, 4 or 8). */
	int64_t disp;        /* Displacement. */
	nmd_x86_memory_operand2(uint8_t segment, uint8_t base, uint8_t index, uint8_t scale, int64_t disp)
		: segment(segment),
		base(base),
		index(index),
		scale(scale),
		disp(disp) {}
};

struct nmd_x86_operand2
{
	uint8_t type;                  /* The operand's type. A member of 'NMD_X86_OPERAND_TYPE'. */
	bool is_implicit;              /* If true, the operand does not appear on the intruction's formatted form. */
	uint8_t action;                /* The action on the operand. A member of 'NMD_X86_OPERAND_ACTION'. */
	union UN {                      /* The operand's "raw" data. */
		uint8_t reg;               /* Register operand. A variable of type 'NMD_X86_REG' */
		int64_t imm;               /* Immediate operand. */
		nmd_x86_memory_operand2 mem;  /* Memory operand. */
		UN() {}
	} fields;

	nmd_x86_operand2()
		: type(0),
		is_implicit(0),
		action(0) {}
	nmd_x86_operand2(uint8_t type, bool is_implicit, uint8_t action, int64_t x)
		: type(type),
		is_implicit(is_implicit),
		action(action) { fields.imm = x; }
	nmd_x86_operand2(uint8_t type, bool is_implicit, uint8_t action, nmd_x86_memory_operand2 mem)
		: type(type),
		is_implicit(is_implicit),
		action(action) { fields.mem = mem; }
};

