	uint8_t buffer[NMD_X86_MAXIMUM_INSTRUCTION_LENGTH];
	nmd_x86_operand2 operands[NMD_X86_MAXIMUM_NUM_OPERANDS];
	nmd_x86_modrm modrm;
	nmd_x86_sib sib;
	uint8_t imm_mask;
