void compare_instructions(const nmd_x86_instruction2& a, const nmd_x86_instruction& b)
{
	EXPECT_EQ(a.valid, b.valid);
	EXPECT_EQ(a.has_modrm, b.has_modrm);
	EXPECT_EQ(a.has_sib, b.has_sib);
	EXPECT_EQ(a.has_rex, b.has_rex);
	EXPECT_EQ(a.rex_w_prefix, b.rex_w_prefix);
	EXPECT_EQ(a.repeat_prefix, b.repeat_prefix);
	EXPECT_EQ(a.repeat_prefix, b.repeat_prefix);
	EXPECT_EQ(a.mode, b.mode);
	EXPECT_EQ(a.length, b.length);
	EXPECT_EQ(a.opcode, b.opcode);
	EXPECT_EQ(a.opcode_size, b.opcode_size);
	EXPECT_EQ(a.id, b.id);
	EXPECT_EQ(a.prefixes, b.prefixes);
	EXPECT_EQ(a.num_prefixes, b.num_prefixes);
	EXPECT_EQ(a.num_operands, b.num_operands);
	EXPECT_EQ(a.group, b.group);
	EXPECT_EQ(memcmp(a.buffer, b.buffer, NMD_X86_MAXIMUM_INSTRUCTION_LENGTH), 0);
	for (int i = 0; i < a.num_operands; i++)
	{
		EXPECT_EQ(a.operands[i].type, b.operands[i].type);
		EXPECT_EQ(a.operands[i].is_implicit, b.operands[i].is_implicit);
		EXPECT_EQ(a.operands[i].action, b.operands[i].action);
		switch (a.operands[i].type)
		{
		case NMD_X86_OPERAND_TYPE_REGISTER: EXPECT_EQ(a.operands[i].fields.reg, b.operands[i].fields.reg); break;
		case NMD_X86_OPERAND_TYPE_IMMEDIATE: EXPECT_EQ(a.operands[i].fields.imm, b.operands[i].fields.imm); break;
		case NMD_X86_OPERAND_TYPE_MEMORY:
		{
			EXPECT_EQ(a.operands[i].fields.mem.segment, b.operands[i].fields.mem.segment);
			EXPECT_EQ(a.operands[i].fields.mem.base, b.operands[i].fields.mem.base);
			EXPECT_EQ(a.operands[i].fields.mem.index, b.operands[i].fields.mem.index);
			EXPECT_EQ(a.operands[i].fields.mem.scale, b.operands[i].fields.mem.scale);
			EXPECT_EQ(a.operands[i].fields.mem.disp, b.operands[i].fields.mem.disp);
			break;
		}
		}
	}
	EXPECT_EQ(a.modrm.modrm, b.modrm.modrm);
	EXPECT_EQ(a.sib.sib, b.sib.sib);
	EXPECT_EQ(a.imm_mask, b.imm_mask);
	EXPECT_EQ(a.disp_mask, b.disp_mask);
	EXPECT_EQ(a.immediate, b.immediate);
	EXPECT_EQ(a.displacement, b.displacement);
	EXPECT_EQ(a.opcode_map, b.opcode_map);
	EXPECT_EQ(a.encoding, b.encoding);
	EXPECT_EQ(a.vex.vex[0], b.vex.vex[0]);
	EXPECT_EQ(a.vex.vex[1], b.vex.vex[1]);
	EXPECT_EQ(a.vex.vex[2], b.vex.vex[2]);
	EXPECT_EQ(a.modified_flags.eflags, b.modified_flags.eflags);
	EXPECT_EQ(a.tested_flags.eflags, b.tested_flags.eflags);
	EXPECT_EQ(a.set_flags.eflags, b.set_flags.eflags);
	EXPECT_EQ(a.cleared_flags.eflags, b.cleared_flags.eflags);
	EXPECT_EQ(a.undefined_flags.eflags, b.undefined_flags.eflags);
	EXPECT_EQ(a.rex, b.rex);
	EXPECT_EQ(a.segment_override, b.segment_override);
	EXPECT_EQ(a.simd_prefix, b.simd_prefix);
}

TEST(assembler_disassembler_ldisasm_test, all_instructions)
{
	nmd_x86_instruction instruction;
	char formatted_instruction[128];
	uint8_t buffer[NMD_X86_MAXIMUM_INSTRUCTION_LENGTH];

	// Test all instructions in the array above.
	for (size_t i = 0; i < _NMD_NUM_ELEMENTS(instructions); i++)
	{
		SCOPED_TRACE("INDEX=" + std::to_string(i) + '(' + instructions[i].s + ')');

		// Test length disassembler
		EXPECT_EQ(nmd_x86_ldisasm(instructions[i].i.buffer, 15, (NMD_X86_MODE)instructions[i].i.mode), instructions[i].i.length);

		// Test decoder
		EXPECT_EQ(nmd_x86_decode(&instructions[i].i.buffer, 15, &instruction, (NMD_X86_MODE)instructions[i].i.mode, NMD_X86_DECODER_FLAGS_ALL), instructions[i].i.valid);
		if (instructions[i].i.valid)
		{
			// Test decoder
			//EXPECT_EQ(memcmp(&instructions[i].i, &instruction, sizeof(nmd_x86_instruction)), 0); // Fast compare(generic)
			compare_instructions(instructions[i].i, instruction); // Slow compare(rich debug info)
						
			// Test formatter
			nmd_x86_format(&instruction, formatted_instruction, NMD_X86_INVALID_RUNTIME_ADDRESS, NMD_X86_FORMAT_FLAGS_DEFAULT);
			EXPECT_EQ(strcmp(formatted_instruction, instructions[i].s), 0);
			
			// Test assembler
			memset(buffer, 0x00, sizeof(buffer));
			EXPECT_EQ(nmd_x86_assemble(formatted_instruction, buffer, sizeof(buffer), NMD_X86_INVALID_RUNTIME_ADDRESS, (NMD_X86_MODE)instructions[i].i.mode, 0), instructions[i].i.length);
			EXPECT_EQ(memcmp(buffer, instructions[i].i.buffer, sizeof(buffer)), 0);
		}
	}
}

// Tests that are impossible using the suite above(e.g. the string is different on input than on output, but they're expected to be different). Or tests that require a different set of format flags
TEST(side_tests_suite, side_tests)
{
	uint8_t buffer[15];
	nmd_x86_instruction i;
	{ EXPECT_EQ(nmd_x86_assemble("push 40",       buffer, 15, -1, NMD_X86_MODE_16, 0), 2); EXPECT_EQ(buffer[0], 0x6a); EXPECT_EQ(buffer[1], 40); }
	{ EXPECT_EQ(nmd_x86_assemble("push 0b101010", buffer, 15, -1, NMD_X86_MODE_32, 0), 2); EXPECT_EQ(buffer[0], 0x6a); EXPECT_EQ(buffer[1], 42); }
	{ EXPECT_EQ(nmd_x86_assemble("push 0x50",     buffer, 15, -1, NMD_X86_MODE_64, 0), 2); EXPECT_EQ(buffer[0], 0x6a); EXPECT_EQ(buffer[1], 0x50); }
	{ EXPECT_EQ(nmd_x86_assemble("push -1",       buffer, 15, -1, NMD_X86_MODE_16, 0), 2); EXPECT_EQ(buffer[0], 0x6a); EXPECT_EQ(buffer[1], 0xff); }
	{ EXPECT_EQ(nmd_x86_assemble("push -0x80",    buffer, 15, -1, NMD_X86_MODE_32, 0), 2); EXPECT_EQ(buffer[0], 0x6a); EXPECT_EQ(buffer[1], 0x80); }
	{ EXPECT_EQ(nmd_x86_assemble("push -3",       buffer, 15, -1, NMD_X86_MODE_64, 0), 2); EXPECT_EQ(buffer[0], 0x6a); EXPECT_EQ(buffer[1], 0xfd); }
	{ *((uint32_t*)buffer) = 0xbbaa6866; EXPECT_EQ(nmd_x86_decode(buffer, 15, &i, NMD_X86_MODE_32, NMD_X86_DECODER_FLAGS_ALL), true); EXPECT_EQ(i.length, 4); EXPECT_EQ(i.imm_mask, NMD_X86_IMM16); }
}

#define EMULATOR_BLOCK_SIZE (1024 * 1024) /* 1MB */

TEST(emulator_test, all_instructions)
{
	nmd_x86_cpu cpu;
	memset(&cpu, 0, sizeof(cpu));
	cpu.mode = NMD_X86_MODE_32;
	cpu.physical_memory = malloc(EMULATOR_BLOCK_SIZE);
	cpu.physical_memory_size = EMULATOR_BLOCK_SIZE;
	cpu.virtual_address = 0x3b000; /* Some random address */
	cpu.rip = cpu.virtual_address + 0x1000; /* Execution starts 0x1000 bytes after the start of the block */
	cpu.rsp.l64 = cpu.virtual_address + 0x48000; /* The bottom of the stack is 0x48000 bytes after the start of the block */

	void* mem = NMD_EMULATOR_RESOLVE_VA(cpu.virtual_address + 0x200);
	*(uint64_t*)mem = 0;

	/* 00 - add Eb, Gb */
	memcpy(NMD_EMULATOR_RESOLVE_VA(cpu.rip), "\x00\x18", 2);
	cpu.rax.l64 = cpu.virtual_address + 0x200;
	*(uint8_t*)mem = 10;
	cpu.rbx.l8 = 27;
	EXPECT_EQ(nmd_x86_emulate(&cpu, 1), true);
	EXPECT_EQ(*(uint8_t*)mem, 37);

	/* 01 - add Gb, Eb */
	memcpy(NMD_EMULATOR_RESOLVE_VA(cpu.rip), "\x01\x18", 2);
	cpu.rbx.l64 = 1234;
	EXPECT_EQ(nmd_x86_emulate(&cpu, 1), true);
	EXPECT_EQ(*(uint32_t*)mem, 1271);

	/* 02 - add Gv, Ev */
	memcpy(NMD_EMULATOR_RESOLVE_VA(cpu.rip), "\x02\x18", 2);
	cpu.rbx.l8 = 11;
	*(uint64_t*)mem = 100;
	EXPECT_EQ(nmd_x86_emulate(&cpu, 1), true);
	EXPECT_EQ(cpu.rbx.l8, 111);

	/* 03 - add Ev, Gv */
	memcpy(NMD_EMULATOR_RESOLVE_VA(cpu.rip), "\x03\x18", 2);
	cpu.rbx.l32 = 472;
	*(uint64_t*)mem = 1000;
	EXPECT_EQ(nmd_x86_emulate(&cpu, 1), true);
	EXPECT_EQ(cpu.rbx.l64, 1472);

	/* 04 - add al, lb */
	memcpy(NMD_EMULATOR_RESOLVE_VA(cpu.rip), "\x04\x20", 2);
	cpu.rax.l8 = 3;
	EXPECT_EQ(nmd_x86_emulate(&cpu, 1), true);
	EXPECT_EQ(cpu.rax.l8, 35);

	/* 05 - add rAX, lv */
	memcpy(NMD_EMULATOR_RESOLVE_VA(cpu.rip), "\x05\x21\x43\x00\x00", 5);
	cpu.rax.l32 = 0x50000;
	EXPECT_EQ(nmd_x86_emulate(&cpu, 1), true);
	EXPECT_EQ(cpu.rax.l32, 0x54321);
}

